import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { RootState } from '../store';

export interface loginState {
    isLogined: boolean;
    userInfo?: iUserInfo;
    status: 'idle' | 'loading' | 'failed';
}

const initialState: loginState = {
    isLogined: false,
    status: 'idle',
    userInfo: {}
};

export interface iUserInfo {
    id?: number;
    userid?: string;
    userpw?: string;
}

export const loginSlice = createSlice({
    name: 'login',
    initialState,
    reducers: {
        userLogined: (state, action: PayloadAction<iUserInfo>) => {
            state.isLogined = true;
            state.userInfo = action.payload;
        },
        userLogouted: (state, action: PayloadAction<iUserInfo>) => {
            state.isLogined = false;
            state.userInfo = action.payload;
        },
    },
});

export const { userLogined, userLogouted } = loginSlice.actions;

export const selectAuth = (state: RootState) => state.user.isLogined;
export default loginSlice.reducer;
