import React, { useState } from 'react';
import styled, { css } from 'styled-components';
import { RiLogoutCircleLine } from 'react-icons/ri';

import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';

import * as apiCaller from '../../util/apiCaller';
import { iUserInfo } from '../redux/features/loginSlice';
import { userLogouted } from '../redux/features/loginSlice';

const CircleButton = styled.button`
    background: #3866d9;
    &:hover {
        background: #6382e6; 
    }

    z-index: 6;
    cursor: pointer;
    width: 50px;
    height: 50px;
    display: block;
    justify-content: center;
    font-size: 60px;
    position: absolute;
    left: 50%;
    bottom: 0px;
    transform: translate(-50%, -1480%);
    color: white;
    border-radius: 50%;
    border: none;
    outline: none;
    display: flex;
    align-items: center;
    justify-content: center;

    transition: 0.125s all ease-in;
`;

function TodoLogout() {
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const onLogin = async () => {
        try {
            const res = await apiCaller.Login(id, pw);
            const authInfo: iUserInfo = {
                id: res.data.id,
                userid: res.data.userid,
                userpw: res.data.userpw,
            };
            if (id === res.data.userid && pw === res.data.userpw) {
                dispatch(userLogouted(authInfo));
                navigate('/todo');
            } else {
                alert('Please check Id or Password again.');
                setId('');
                setPw('');
            }
        } catch (ex) {
            alert('Failed.');
        }
    };

    return (
        <>
            <CircleButton>
                <RiLogoutCircleLine />
            </CircleButton>
        </>
    );
}

export default TodoLogout;
