import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components';
import { IoMdLogIn } from 'react-icons/io';

import * as apiCaller from '../../util/apiCaller';
import { iUserInfo } from '../../redux/features/loginSlice';
import { userLogined } from '../../redux/features/loginSlice';

const TodoHeadBlock = styled.div`
    h1 {
        margin: 0;
        font-size: 36px;
        color: #343a40;
        text-align: center;
    }
    padding-top: 30px;
    padding-left: 32px;
    padding-right: 32px;
    padding-bottom: 30px;
    border-bottom: 1px solid #e9ecef;
`;

const LoginButton = styled.button`
    background: #3866d9;
    &:hover {
        background: #6382e6;
    }
    &:active {
        background: #2050c9;
    }

    z-index: 5;
    cursor: pointer;
    width: 80px;
    height: 80px;
    display: block;
    align-items: center;
    justify-content: center;
    font-size: 60px;
    position: absolute;
    left: 50%;
    bottom: 0px;
    transform: translate(-50%, 50%);
    color: white;
    border-radius: 50%;
    border: none;
    outline: none;
    display: flex;
    align-items: center;
    justify-content: center;

    transition: 0.125s all ease-in;
`;

const InsertFormPositioner = styled.div`
    h2 {
        margin: 0;
        padding-left: 45px;
        padding-right: 32px;
        font-size: 30px;
        color: #343a40;
    }
    width: 100%;
    bottom: 0;
    left: 0;
    position: absolute;
`;

const InsertId = styled.form`
    padding-left: 32px;
    padding-top: 15px;
    padding-right: 32px;
    padding-bottom: 70px;

    border-bottom-left-radius: 16px;
    border-bottom-right-radius: 16px;
`;

const InsertPw = styled.form`
    padding-left: 32px;
    padding-top: 15px;
    padding-right: 32px;
    padding-bottom: 170px;

    border-bottom-left-radius: 16px;
    border-bottom-right-radius: 16px;
`;

const Input = styled.input`
    padding: 12px;
    border-radius: 4px;
    border: 1px solid #dee2e6;
    width: 100%;
    outline: none;
    font-size: 18px;
    box-sizing: border-box;
`;

function TodoLogin() {
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const [id, setId] = useState('');
    const [pw, setPw] = useState('');

    const onLogin = async () => {
        try {
            const res = await apiCaller.Login(id, pw);
            const userInfo: iUserInfo = {
                id: res.data.id,
                userid: res.data.userid,
                userpw: res.data.userpw,
            };
            const users = useSelector((state) => state);
            if (id === res.data.userid && pw === res.data.userpw) {
                dispatch(userLogined(userInfo));
                navigate('/todo');
            } else {
                alert('Please check Id or Password again.');
                setId('');
                setPw('');
            }
        } catch (ex) {
            alert('Failed.');
        }
    };

    return (
        <>
            <TodoHeadBlock>
                <h1>Login</h1>
            </TodoHeadBlock>
            <InsertFormPositioner>
                <h2>Id</h2>
                <InsertId>
                    <Input placeholder="Id" onChange={(e) => setId(e.target.value)}></Input>
                </InsertId>
                <h2>Password</h2>
                <InsertPw>
                    <Input placeholder="Password" onChange={(e) => setPw(e.target.value)}></Input>
                </InsertPw>
            </InsertFormPositioner>
            <LoginButton onClick={onLogin}>
                <IoMdLogIn />
            </LoginButton>
        </>
    );
}

export default React.memo(TodoLogin);
